        int mx=0,mn=n+1;
        rep(j,i,p) mx=max(mx,a[j]),mn=min(mn,a[j]);
        if(mx!=p||mn!=i) return puts("No"),0;
        int f1=0,f2=0;
        fo(j,i,p,2)
            if(a[j]>f1) f1=a[j];
            else if(a[j]>f2) f2=a[j];
            else return puts("No"),0;
        i=p+1;
    }
    puts("Yes");
    return 0;
}

