      cerr << endl;
    }
    if (even.begin()->second == bias) {
      even.erase(even.begin());
      swap(even, odd);
      bias++;
      continue;
    }
    assert (odd.size() > 0);
    if (odd.begin()->second != bias + 1) {
      cout << "No\n";
      return 0;
    }
    int zeroidx = inv[bias];
    even.erase(PI(zeroidx, bias));
    swap(even, odd);
    bias++;
  }
