il ll mergesort(int l,int r,int*a){
	if(l==r)return 0;static int a1[Size];
	int mid(l+r>>1),i(l),j(mid+1),k(l);
	ll ans(mergesort(l,mid,a)+mergesort(mid+1,r,a));
	while(i<=mid&&j<=r)
		a1[k++]=a[i]<a[j]?a[i++]:(ans+=mid-i+1,a[j++]);
	while(i<=mid)a1[k++]=a[i++];while(j<=r)a1[k++]=a[j++];
	for(i=l;i<=r;++i)a[i]=a1[i];return ans;
}
