		tag0[i]+=tag0[i-1];
		tag1[i]+=tag1[i-1];
		if(i&1)
			tag[i]=tag1[i];
		else
			tag[i]=tag0[i];
		if(tag[i]&&tag[i-1])
			return puts("No"),0;
	}
	for(i=1;i<=n;++i){
		if(!tag[i]) continue;
		for(j=i;j<=n;++j)
			if(tag[j]&&!tag[j+1]&&!tag[j+2])
				break;
		l=max(1,i-1);r=min(n,j+1);
		for(k=l;k<=r;++k)
			if(a[k]<l||a[k]>r)
				return puts("No"),0;
		lstl=lstr=0;
		for(k=l;k<=r;++k)
			if(a[k]!=k){
				if(a[k]<k){
				//	printf("lstl %d %d\n",lstl,a[k]);
					if(lstl>a[k]) return puts("No"),0;
					lstl=a[k]; 
				} 
				else{
				//	printf("lstr %d %d\n",lstr,a[k]);
					if(lstr>a[k]) return puts("No"),0;
					lstr=a[k];
				}
			}
		i=j;
	}
	puts("Yes");
	return 0;
}

