bool jud(int s)
{
	fo(i,s,n)
	{
		if(i == n || state[i+1] != 0) return 0;
		int l = i, r = i+1, lasV = -inf;
		while(r+2 <= n && state[r+2] == 0) r += 2;
		r = min(n, r+1); i = r;
		fo(j,l,r) 
		{
			if(state[j] == 1)
			{
				if(lasV > a[j]) return 0;
				lasV = a[j];
			}
		}
		fo(j,l,r) if(state[j] == 0) gmax(lasV, a[j]);
		if(lasV != r) return 0;
		lasV = inf;
		fd(j,r,l)
		{
			if(state[j] == -1)
			{
				if(lasV < a[j]) return 0;
				lasV = a[j];
			}
		}
		fo(j,l,r) if(state[j] == 0) gmin(lasV, a[j]);
		if(lasV != l) return 0;
	}
	return 1;
}
