int main(){
	cin.tie(0);
	ios::sync_with_stdio(false);		//DON'T USE scanf/printf/puts !!
	cout << fixed << setprecision(20);

	int N; cin >> N;
	set<V<int>> st;
	{
		V<int> s(N); iota(all(s),0);
		queue<V<int>> q; q.push(s); st.insert(s);
		while(!q.empty()){
			auto p = q.front(); q.pop();
			rep1(i,N-2){
				if(p[i-1]<p[i]&&p[i]<p[i+1]){
					auto r = p;
					swap(r[i-1],r[i+1]);
					if(!st.count(r)){
						st.insert(r);
						q.push(r);
					}
				}
			}
		}
		// show("--------- OK ");
		// for(auto p: st) show(p);
		// show("--------- NG");
		// do{
		// 	bool ok = true;
		// 	rep(i,N) if(s[i]%2 != i%2) ok = false;
		// 	if(!ok) continue;
		// 	if(!st.count(s)) show(s);
		// }while(next_permutation(all(s)));
	}
	auto brute = [&](V<int> p){
		return st.count(p);
	};
	auto naive = [&](V<int> p){
		N = p.size();
		rep(i,N) if(p[i]%2 != i%2) return false;
		rep(v,N){
			int x = 0;
			rep(i,N) if(p[i] == v) x = i;
			while(x != v){
				if(p[x-2] > p[x-1] && p[x-1] > p[x]){
					swap(p[x-2],p[x]); x -= 2;
				}else{
					return false;
				}
			}
		}
		return true;
	};
	auto solve = [&](V<int> p){
		rep(i,N) if(p[i]%2 != i%2) return false;
//		show("------------_");show(p);
		int mn = 1e9, mx = -1;
		int L = 0;
		rep(i,N){
			chmin(mn,p[i]); chmax(mx,p[i]);
			if(mn == L && mx == i){
				int R = i;	//[L,R]
//				show(L);show(R);
				int n = R-L+1;
				V<int> v(n); rep(i,n) v[i] = p[i+L] - L;
				if(n > 1){
					assert(p[L] != L && p[R] != R);		//xoxoxoxox
					if(n%2 == 0) return false;
					rep(i,n) if(i%2==1 && v[i] != i) return false;
					V<int> ls,rs;
					rep(i,n) if(i%2 == 0){
						if(v[i] == i) return false;
						if(v[i] < i) rs.pb(v[i]);
						else ls.pb(v[i]); 
					}
					rep(i,si(ls)-1) if(ls[i] > ls[i+1]) return false;
					rep(i,si(rs)-1) if(rs[i] > rs[i+1]) return false;
				}

				mn = 1e9, mx = -1;
				L = i+1;
			}
		}
		assert(mx == -1);
		return true;
	};

	if(false){
		V<int> v(N); iota(all(v),0);
		do{
			if(brute(v) != solve(v)){
				show(v);
				show(brute(v)); show(solve(v));
				assert(false);
			}
		}while(next_permutation(all(v)));
		return 0;
	}

	V<int> p(N);
	rep(i,N) cin >> p[i], p[i]--;
	if(solve(p)) cout << "Yes" << endl;
	else cout << "No" << endl;
}

