vector<int> L,R;
bool cheak(int l,int r){
	L.clear();R.clear();
	for(int i=l+1;i<=r;i+=2) if(idx[i]!=i) return 0;
	for(int i=l;i<=r;i+=2){
		if(idx[i]<=i) R.push_back(idx[i]);
		else L.push_back(idx[i]);
	}
	for(int i=0;i<L.size();i++)if(L[i]>L[i+1])return 0;
	for(int i=0;i<R.size();i++)if(R[i]>R[i+1])return 0;
	return 1;
}
