            mi[k] = min(mi[k + 1], stk[k]);
        int mx = stk[1];
        for (int k = 2; k < top; ++k) {
            mx = max(mx, stk[k]);
            if (mi[k] < stk[k] && stk[k] < mx)
                return puts("No"), 0;
        }
    }

    return puts("Yes"), 0;
}

