int P[300000];
int Q[300000];
bool sub(int l, int r) {
	for (int i = l; i < r; ++i) {
		Q[P[i]] = i;
	}

	int i = l;
	while (i < r - 2 && Q[i] > Q[i + 1] && Q[i + 1] > Q[i + 2]) {
		swap(Q[i], Q[i + 2]);
		i += 2;
	}

	int j = r - 1;
	while (j > l + 1 && Q[j - 2] > Q[j - 1] && Q[j - 1] > Q[j]) {
		swap(Q[j - 2], Q[j]);
		j -= 2;
	}
	bool ok = true;
	REP(i, l, r) {
		if (Q[i] != i) ok = false;
	}
	return ok;
}

int main() {
	int N;
	cin >> N;
	REP(i, 0, N) {
		cin >> P[i];
		--P[i];
	}

	int mx = 0;
	int l = 0;
	int r = 0;

	bool ok = true;
	REP(i, 0, N) {
		mx = max(mx, P[i]);
		if (mx == i) {
			r = i + 1;
			ok &= sub(l, r);
			l = r;
		}
	}
	YesNo(ok);
	return 0;
}

