template<int maxn,typename NUM>
struct BIT {
    NUM s[maxn];

    void init() {
        mst(s, 0);
    }
    BIT() {
        init();
    }

    void update(int a,NUM value) {
        a++;
        while(a<maxn) {
            s[a]+=value;
            a+=lowbit(a);
        }
    }

    NUM query(int a) {
        NUM ans = 0;
        a++;
        while(a) {
            ans+=s[a];
            a-=lowbit(a);
        }
        return ans;
    }
    NUM query(int a,int b) {
        if(b<a)return 0;
        return query(b)-query(a-1);
    }
};

