bool work(int &n) {
	int p = 0;
	for (int i = n; i; --i)
		if (a[i] == n) { p = i; break; }
	if (p == 0) exit(1);
	if (p == n) return --n, true;
	if ((n - p) & 1) return false;
	for (int i = p + 1; i <= n; i += 2)
		if (a[i] < a[i + 1]) return false;
	for (int i = p; i < n; ++i) a[i] = a[i + 1];
	for (int i = p; i < n; i += 2) swap(a[i], a[i + 1]);
	for (int i = p + 1; i < n; ++i) if (a[i] != i) return false;
	return n = p, true;
}

