const int INF = 1000000000;
int val[1 << 19];

class BIT {
public:
	vector<int> bit;
	int size_;

	void init(int sz) {
		size_ = sz + 2;
		bit.resize(size_ + 2, 0);
	}
	void add(int pos, int x) {
		pos++;
		while (pos <= size_) {
			bit[pos] += x;
			pos += (pos & -pos);
		}
	}
	int sum(int pos) {
		int s = 0; pos++;
		while (pos >= 1) {
			s += bit[pos];
			pos -= (pos & -pos);
		}
		return s;
	}
};

bool solve(vector<int> Z) {
	BIT B1; B1.init(Z.size() + 2);
	for (int i = Z.size() - 1; i >= 0; i--) {
		val[i] = i + B1.sum(Z[i]);
		B1.add(Z[i], 1);
	}
	BIT B2; B2.init(Z.size() + 2);
	for (int i = 0; i < Z.size(); i++) {
		int score = B1.sum(val[i] - 1) - B1.sum(Z[i]);
		if (score >= 1) return false;
		B2.add(Z[i], 1);
	}
	return true;
}

