        arr[i] = x;
    }
    if (!c) {
        puts("No");
        return 0;
    }

    int cur = n;
    for (int i = n; i > 0; --i) {
        if (arr[i] == cur) {
            if (i == cur) {
                --cur;
                continue;
            }
            int j = i;
            while (j != cur) {
                if (arr[j] > arr[j + 1] && arr[j + 1] > arr[j + 2]) {
                    swap(arr[j], arr[j + 2]);
                    j += 2;
                }
            }
            j = cur;
            while (j > i) {
                if (arr[j] != j) {
                    c = 0;
                    i = 0;
                    break;
                }
                --j;
            }
            cur = i;
            ++i;
        }
    }
    if (c) puts("Yes");
    else puts("No");
}

