int proc(vector<int> cand) {
	if(cand.empty()) return 1;
	vector<int> L,R;
	FORR(c,cand) if(A[c]!=c) return 0;

	int i;
	for(i=cand[0]-1;i<=cand.back()+1;i+=2) {
		if(A[i]==i) return 0;{
		if(A[i]<i) R.push_back(A[i]);
		else L.push_back(A[i]);
	}

	FOR(i,(int)L.size()-1) if(L[i]>L[i+1]) return 0;
	FOR(i,(int)R.size()-1) if(R[i]>R[i+1]) return 0;
	return 1;

}

void solve() {
	int i,j,k,l,r,x,y; string s;

	cin>>N;
	for(i=1;i<=N;i++) {
		cin>>A[i];

		if(abs(i-A[i])%2==1) return _P("No\n");
		if(i<A[i]) {
			did[i+1]++;
			did[A[i]+1]--;
		}
		else {
			did[A[i]+1]++;
			did[i+1]--;
		}

	}

	vector<int> cand;
	for(i=1;i<=N;i++) {
		if(i>=2) did[i]+=did[i-2];
		if(did[i]) {
			if(did[i-1]) return _P("No\n");
			if(cand.size() && cand.back()+2!=i) {
				if(!proc(cand)) return _P("No\n");
				cand.clear();
			}
			cand.push_back(i);
		}
	}
	if(!proc(cand)) return _P("No\n");
	cout<<"Yes"<<endl;

}


