const int INF = 1000000000;
int val[1 << 19];

class BIT {
public:
	vector<int> bit;
	int size_;

	void init(int sz) {
		size_ = sz + 2;
		bit.resize(size_ + 2, 0);
	}
	void add(int pos, int x) {
		pos++;
		while (pos <= size_) {
			bit[pos] += x;
			pos += (pos & -pos);
		}
	}
	int sum(int pos) {
		int s = 0; pos++;
		while (pos >= 1) {
			s += bit[pos];
			pos -= (pos & -pos);
		}
		return s;
	}
};

bool solve(vector<int> Z) {
	BIT B1; B1.init(Z.size() + 2);
	for (int i = Z.size() - 1; i >= 0; i--) {
		val[i] = i + B1.sum(Z[i]);
		B1.add(Z[i], 1);
	}
	BIT B2; B2.init(Z.size() + 2);
	for (int i = 0; i < Z.size(); i++) {
		int score = B2.sum(val[i] - 1) - B2.sum(Z[i]);
		if (score >= 1) return false;
		B2.add(Z[i], 1);
	}
	return true;
}

int N, A[1 << 19];
int D[1 << 19];

int main() {
	scanf("%d", &N);
	for (int i = 1; i <= N; i++) {
		scanf("%d", &A[i]);
		D[i] = max(D[i - 1], A[i]);
	}
	vector<int> R; R.push_back(0);
	for (int i = 1; i <= N; i++) {
		if (D[i] == i) R.push_back(i);
	}
	for (int i = 0; i < R.size() - 1; i++) {
		vector<int> E;
		for (int j = R[i] + 1; j <= R[i + 1]; j++) E.push_back(A[j]);

		bool flag = false;
		for (int j = 1; j < E.size(); j += 2) {
			if (E[j] != R[i] + j + 1) flag = true;
		}
		if (flag == true) {
			cout << "No" << endl;
			return 0;
		}

		vector<int> F;
		for (int j = 0; j < E.size(); j += 2) F.push_back((E[j] - R[i]) / 2);

		bool ret = solve(F);
		if (ret == false) {
			cout << "No" << endl;
			return 0;
		}
	}

	cout << "Yes" << endl;
	return 0;
}

