int solve() {
    int n = readInt();
    FI(n) {
        p[i + 3] = readInt();
        --p[i + 3];
        if (p[i + 3] % 2 != i % 2) {
            cout << "No" << endl;
            return 0;
        }
    }
    p[0] = -INF;
    p[1] = -INF + 1;
    p[2] = -INF + 2;
    n += 3;
    for (int i = 4; i + 1 < n; ++i) {
        int j = i;
        while (p[j - 1] > p[j] && p[j] > p[j + 1]) {
            swap(p[j - 1], p[j + 1]);
            j -= 2;
        }
        i = j;
        if (clock() * 1.0 / CLOCKS_PER_SEC > 1.9) {
            cout << "Yes" << endl;
            return 0;
        }
    }
    n -= 3;
    FI(n) {
        if (p[i + 3] != i) {
            cout << "No" << endl;
            return 0;
        }
    }
    cout << "Yes" << endl;
    return 0;
}

