template <class T = long long int>
class Bit {
    private:
    T len;
    T *arr;

    public:
    Bit(T l, T initialValue = 0) : len(l) {
        arr = new T[l+1];
        for (T i = 0; i < l + 1; i++) {
            arr[i] = initialValue;
        }
    }
    ~Bit() {
        delete[] arr;
    }

    void update(T a, T newval) {
        for (T x = a; x <= len; x += x & -x) arr[x] += newval;
    }
    T query(T a) const {
        T ret = 0;
        for (T x = a; x > 0; x -= x & -x) ret += arr[x];
        return ret;
    }
};

