        if (!q.size()) continue;
        // for (auto el : q) std::cout << el << " ";
        // std::cout << std::endl;

        Bit<int> b(1000000);
        int inv = 0;
        for (auto el : q) {
            inv += b.query(el);
            b.update(el, 1);
        }
        inv = q.size() * (q.size() - 1) / 2 - inv;
        // std::cout << "inv = " << inv << std::endl;
        if (inv > q.size() - 1) {
            std::cout << "No" << std::endl;
            return 0;
        }
    }

    std::cout << "Yes" << std::endl;

    return 0;
}

