void check(ll st, ll en, vll &p){
	if (st == en) bye("No");

	//全数字そろっているか
	vll buf;
	for (ll idx = st-1; idx<=en-1; idx+=2){
		buf.push_back(p[idx]);
	}
	sort(buf.begin(), buf.end());
	REP(j, (ll)buf.size()){
		if (buf[j] != st+j*2) bye("No");
	}

	//上ずれと下ずれが昇順か
	vll upper, lower;
	for (ll idx = st-1; idx<=en-1; idx+=2){
		ll i = idx+1;
		if (p[idx] > i) upper.push_back(p[idx]);
		else            lower.push_back(p[idx]);
	}
	REP(j, (ll)upper.size()-1){
		if (upper[j] > upper[j+1]) bye("No");
	}
	REP(j, (ll)lower.size()-1){
		if (lower[j] > lower[j+1]) bye("No");
	}
}


void solve()
{
	ll N;
	cin >> N;
	vll p = cinv(N);

	ll st = -1;
	ll en = -1;
	REP(idx, N){
		ll i = idx+1;
		if (p[idx] == i) continue;
		if (st == -1){
			st = en = i;
			continue;
		}

		if (en+2 == i){ //1つ飛び
			en = i;
			continue;
		}

		//ここでst～enが一組に確定

		check(st,en, p);

		st = en = i;
	}
	check(st,en,p);

	cout << "Yes" << '\n';
}

int main(){
	solve();
	return 0;
}

