    for (l += n, r += n; l < r; l >>= 1, r >>= 1) {
        if (l & 1) ret += t[l++];
        if (r & 1) ret += t[--r];
    }
    return ret;
}
void init() {
    rep(i, n) t2[i + n] = p[i];
    for (int i = n - 1; i > 0; --i) t2[i] = t2[i << 1] + t2[i << 1 | 1];
}
ll qry2(int l, int r) {
    ll retl = 0, retr = 0;
    for (l += n, r += n; l < r; l >>= 1, r >>= 1) {
        if (l & 1) retl = max(retl, t[l++]);
        if (r & 1) retr = max(retr, t[--r]);
    }
    return max(retl, retr);
}
bool ispos(int l, int r) {
    if ((r - l) % 2 == 0 || p[l] != r) return 0;
    for (int i = l; i + 2 < r; i += 2) {
        if (!(p[i + 1] > p[i + 2])) return 0;
        swap(p[i], p[i + 2]);
    }
    for (int i = l; i < r; i++) {
        if (p[i] != i + 1) return 0;
    }
    return 1;
}

