    }
    if (chk2) {
        cout << "No\n";
        return 0;
    }
    init();
    int prv = 0;
    int ans = 1;
    rep(i, n) {
        if (qry2(prv, i + 1) == i + 1) {
            ans &= ispos(prv, i + 1);
            prv = i + 1;
        }
    }
    ans &= ispos(prv, n);
    cout << (ans ? "Yes\n" : "No\n");
}

