LL merge_sort(int l,int r){
	if(l==r) return 0;
	int i,j,k,mid=(l+r)>>1;
	LL ret=merge_sort(l,mid)+merge_sort(mid+1,r);
	for(i=l,j=mid+1,k=l;i<=mid&&j<=r;){
		if(b[i]>b[j]){
			ret+=mid-i+1;
			c[k++]=b[j++];
		}else{
			c[k++]=b[i++];
		}
	}
	while(i<=mid) c[k++]=b[i++];
	while(j<=r) c[k++]=b[j++];
	return ret;
}
