import java.io.OutputStream;
import java.io.IOException;
import java.io.InputStream;
import java.io.OutputStream;
import java.io.PrintWriter;
import java.io.BufferedWriter;
import java.io.Writer;
import java.io.OutputStreamWriter;
import java.util.InputMismatchException;
import java.io.IOException;
import java.io.InputStream;

/**
 * Built using CHelper plug-in
 * Actual solution is at the top
 */
public class Main {
    public static void main(String[] args) {
        InputStream inputStream = System.in;
        OutputStream outputStream = System.out;
        InputReader in = new InputReader(inputStream);
        OutputWriter out = new OutputWriter(outputStream);
        FRevengeOfBBuBBBlesort solver = new FRevengeOfBBuBBBlesort();
        solver.solve(1, in, out);
        out.close();
    }

    static class FRevengeOfBBuBBBlesort {
        public void solve(int testNumber, InputReader in, OutputWriter out) {
            int n = in.readInt();

            int[] p = in.readIntArray(n);
            MiscUtils.decreaseByOne(p);

            for (int i = 2; i < n; i++) {
                if (p[i - 2] > p[i - 1] && p[i - 1] > p[i]) {
                    for (int j = i; j >= 2 && p[j - 2] > p[j - 1] && p[j - 1] > p[j]; j--) {
                        int tmp = p[j];
                        p[j] = p[j - 2];
                        p[j - 2] = tmp;
                    }
                }
            }

            for (int i = 0; i < n; i++) {
                if (p[i] != i) {
                    out.printLine("No");
                    return;
                }
            }
            out.printLine("Yes");
        }

    }

    static class OutputWriter {
        private final PrintWriter writer;

        public OutputWriter(OutputStream outputStream) {
            writer = new PrintWriter(new BufferedWriter(new OutputStreamWriter(outputStream)));
        }

        public OutputWriter(Writer writer) {
            this.writer = new PrintWriter(writer);
        }

        public void print(Object... objects) {
            for (int i = 0; i < objects.length; i++) {
                if (i != 0) {
                    writer.print(' ');
                }
                writer.print(objects[i]);
            }
        }

        public void printLine(Object... objects) {
            print(objects);
            writer.println();
        }

        public void close() {
            writer.close();
        }

    }

    static class MiscUtils {
        public static void decreaseByOne(int[]... arrays) {
            for (int[] array : arrays) {
                for (int i = 0; i < array.length; i++) {
                    array[i]--;
                }
            }
        }

    }

    static class InputReader {
        private InputStream stream;
        private byte[] buf = new byte[1024];
        private int curChar;
        private int numChars;
        private InputReader.SpaceCharFilter filter;

        public InputReader(InputStream stream) {
            this.stream = stream;
        }

        public int[] readIntArray(int size) {
            int[] array = new int[size];
            for (int i = 0; i < size; i++) {
                array[i] = readInt();
            }
            return array;
        }

        public int read() {
            if (numChars == -1) {
                throw new InputMismatchException();
            }
            if (curChar >= numChars) {
                curChar = 0;
                try {
                    numChars = stream.read(buf);
                } catch (IOException e) {
                    throw new InputMismatchException();
                }
                if (numChars <= 0) {
                    return -1;
                }
            }
            return buf[curChar++];
        }

        public int readInt() {
            int c = read();
            while (isSpaceChar(c)) {
                c = read();
            }
            int sgn = 1;
            if (c == '-') {
                sgn = -1;
                c = read();
            }
            int res = 0;
            do {
                if (c < '0' || c > '9') {
                    throw new InputMismatchException();
                }
                res *= 10;
                res += c - '0';
                c = read();
            } while (!isSpaceChar(c));
            return res * sgn;
        }

        public boolean isSpaceChar(int c) {
            if (filter != null) {
                return filter.isSpaceChar(c);
            }
            return isWhitespace(c);
        }

        public static boolean isWhitespace(int c) {
            return c == ' ' || c == '\n' || c == '\r' || c == '\t' || c == -1;
        }

        public interface SpaceCharFilter {
            public boolean isSpaceChar(int ch);

        }

    }
}

import java.util.Scanner;

class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);

        int N = Integer.parseInt(scanner.nextLine());
        int[] array = new int[N];

        for (int i = 0; i < N; ++i) {
            array[i] = Integer.parseInt(scanner.nextLine());
        }

        boolean yes = true;
        for (int i = 0; i < N; ++i) {
            if (array[i] != i + 1) {
                i = check(array, i);
                if (i < 0) {
                    yes = false;
                    break;
                }
            }
        }

        System.out.println(yes ? "Yes" : "No");
    }

    private static int check(int[] array, int i) {
        int max = array[i];
        int start = i + 1;

        for (int j = 1; i + j < array.length; ++j) {
            if (j % 2 == 0) {
                int t = array[j + i];

                if ((t - start) % 2 == 0) {
                    if (t < start) {
                        return -1;
                    } else if (t > max) {
                        max = t;
                    } else if (i + j + 1 == max) {
                        return max - 1;
                    }
                } else {
                    return -1;
                }
            } else {
                if (array[j + i] != i + j + 1) {
                    return -1;
                }
            }
        }

        return -1;
    }
}

