			} else {
				a ~= p[i]-s;
			}
			prev = true;
		} else {
			if (!prev) {
				if (!judge(a)) {
					writeln("No");
					return;
				}
				a = new int[0];
				s = i;
				a ~= p[i]-i;
			} else {
				a ~= p[i]-s;
			}
			prev = false;
		}
	}
	if (!judge(a)) {
		writeln("No");
	} else {
		writeln("Yes");
	}
}

