use proconio::{input, marker::Usize1};

trait OrdTools: Ord + Clone + Sized {
    fn chmin(&mut self, x: Self) {
        *self = std::cmp::min(self.clone(), x);
    }
    fn chmax(&mut self, x: Self) {
        *self = std::cmp::max(self.clone(), x);
    }
}

impl<T: Ord + Clone + Sized> OrdTools for T {}

fn err() -> ! {
    println!("No");
    std::process::exit(0);
}

trait Itertools2: Iterator {
    fn is_sorted2(self) -> bool
    where
        Self: Sized,
        Self::Item: PartialOrd,
    {
        self.is_sorted_by2(PartialOrd::partial_cmp)
    }

    fn is_sorted_by2<F>(mut self, mut compare: F) -> bool
    where
        Self: Sized,
        F: FnMut(&Self::Item, &Self::Item) -> Option<std::cmp::Ordering>,
    {
        let mut last = match self.next() {
            Some(e) => e,
            None => return true,
        };

        while let Some(curr) = self.next() {
            if let Some(std::cmp::Ordering::Greater) | None = compare(&last, &curr) {
                return false;
            }
            last = curr;
        }

        true
    }
}

impl<I: Iterator> Itertools2 for I {}

fn main() {
    input!(n: usize, p: [Usize1; n]);
    let mut l;
    let mut r = 0;
    let mut max = 0;
    while r < n {
        l = r;
        r = l + 1;
        max.chmax(p[l]);
        while r < n && r - 1 < max {
            max.chmax(p[r]);
            r += 1;
        }
        if l + 1 != r
            && ((r - l) % 2 == 0
                || (l..r).any(|i| (i - l) % 2 == 1 && i != p[i])
                || (l..r).any(|i| (i - l) % 2 == 0 && i == p[i])
                || !(l..r)
                    .filter(|&i| (i - l) % 2 == 0 && i < p[i])
                    .map(|i| p[i])
                    .is_sorted2()
                || !(l..r)
                    .filter(|&i| (i - l) % 2 == 0 && i > p[i])
                    .map(|i| p[i])
                    .is_sorted2())
        {
            err();
        }
    }
    println!("Yes");
}

