typedef long long ll;

const ll MOD = 998244353;
const int MX = 250005;
const ll R = 1231231;
const ll IR = 981323583;

ll qpow(ll x, ll t)
{
	ll ans = 1;
	while(t)
	{
		if(t & 1) ans = ans*x % MOD;
		x = x*x % MOD;
		t >>= 1;
	}
	return ans;
}

ll inv(ll x)
{
	return qpow(x, MOD-2);
}

int n;
char str[MX];

void input()
{
	scanf("%d", &n);
	scanf("%s", str+1);
}

ll con[MX], sum[MX];
map<ll, int> hav;

void work()
{
	ll p = 1;
	for(int i=1; i<=n; i++)
	{
		if(str[i] == '+') con[i] = p;
		else if(str[i] == '-') con[i] = (MOD-p) % MOD;
		else if(str[i] == '>') p = p*R % MOD;
		else p = p*IR % MOD;
		sum[i] = (sum[i-1] + con[i]) % MOD;
		hav[sum[i]]++;
	}
	ll ans = 0, t = 1;
	for(int i=1; i<=n; i++)
	{
		ans += hav[(sum[n]*t+sum[i-1]) % MOD];
		if(str[i] == '>') t = t*R % MOD;
		else if(str[i] == '<') t = t*IR % MOD;
		hav[sum[i]]--;
	}
	printf("%lld\n", ans);
}

int main()
{
	input();
	work();
	return 0;
}

