const int N = 280005;
const int mod = 1e9 + 123;

int pw[N + N];
int e[N];
int h[N];
int n;
string s;

int inverse(int a) {
	int b = mod - 2;
	int ret = 1;
	while(b) {
		if (b & 1) ret = 1LL * ret * a % mod; a = 1LL * a * a % mod;
		b >>= 1;
	}
	return ret;
}

int main() {
	ios_base::sync_with_stdio(false);
	cin.tie(0);

	cin >> n >> s;
	s = ' ' + s;

	pw[0] = 1;
	for (int i = 1; i < N + N; ++i) {
		pw[i] = 1LL * pw[i - 1] * N % mod;
	}

	e[0] = N;
	h[0] = 0;
	for (int i = 1; i <= n; ++i) {
		h[i] = h[i - 1];
		e[i] = e[i - 1];
		if (s[i] == '<' || s[i] == '>') {
			e[i] += (s[i] == '>' ? +1 : -1);
		}
		else {
			h[i] = (h[i] + (s[i] == '+' ? +1 : -1) * pw[e[i]]) % mod;
			if (h[i] < 0) h[i] += mod;
		}
	}

	long long res = 0;
	int t = 1LL * h[n] * inverse(pw[N]) % mod;
	map <int, int> mp;
	mp[1LL * t * pw[N] % mod]++;
	for (int i = 1; i <= n; ++i) {
		res += mp[h[i]];
		mp[(1LL * t * pw[e[i]] + h[i]) % mod]++;
	}

