Int pow(Int x, Int y, Int M){
	if(y == 0)return 1;
	Int res = pow(x, y/2, M);
	res = res * res % M;
	if(y % 2)res *= x;
	return x % M;
}

Int rev(Int x, Int M){
	return pow(x, M-2, M);
}

int main(){
	Int p = 0;
	Int tmp = 0;
	cin >> n;
	cin >> str;
	for(int i = 0;i < n;i++){
		if(str[i] == '+')tmp = (tmp + pow(1000000, p, P1))%P1;
		if(str[i] == '-')tmp = (tmp - pow(1000000, p, P1))%P1;
		if(str[i] == '>')p++;
		if(str[i] == '<')p--;
		sum[i+1] = tmp;
		pos[i+1] = p;

	}
	Int last = sum[n];
	for(int i = n;i >= 0;i--){
		Int tmp = ((last - last * pow(1000000, pos[i], P1) % P1) % P1 + P1)% P1;
		res += cs[(tmp - sum[i] + P1) % P1];
		Int c = (last - sum[i] +P1) % P1;
