int mul(int a, int b){
	return (a * 1ll * b) % MOD;
}

int add(int a, int b){
	a += b;
	a %= MOD;
	a += MOD;
	a %= MOD;
	return a;
}

int minu(int a, int b){
	a -= b;
	a %= MOD;
	a += MOD;
	a %= MOD;
	return a;
}

int POW(int a, int b){
	int res = 1;
	while (b){
		if (b & 1) res = mul(res, a);
		b >>= 1;
		a = mul(a, a);
	}
	return res;
}

