	{
		ind[i]=p,val[i]=0;

		if(s[i]=='<')p--;
		else if(s[i]=='>')p++;
		else if(s[i]=='+')val[i]=pow[p];
		else val[i]=-pow[p];
	}

	for(int i=1;i<=n;i++)
		sum[i]=((sum[i-1]+val[i])%MOD+MOD)%MOD;
}

std::map<int,int> G;

