{
	ans=0;
	for(int i=n,x,y;i;i--)
	{
		G[sum[i]]++;

		x=((ll)sum[n]*pow[ind[i]]%MOD+MOD)%MOD;
		y=((x+sum[i-1])%MOD+MOD)%MOD;

		ans+=G[y];
	}
}

