	for (int i = mid + 1; i <= R; i++)
	{
		if (arr[i] == 0)
		{
			trans.fi *= INV;
			trans.se *= INV;
		}
		if (arr[i] == 1)
		{
			trans.fi *= CO;
			trans.se *= CO;
		}
		if (arr[i] == 2)
		{
			trans.se++;
		}
		if (arr[i] == 3)
		{
			trans.se--; trans.se += INF;
		}
		trans.fi %= INF;
		trans.se %= INF;
		// cerr << "from " << mid + 1 << " to " << i << " is " << trans.fi << "x + " << trans.se << endl;
		ll dif = S - trans.se + INF;
		nmod(dif, INF);
		dif *= modinv(trans.fi);
		dif %= INF;
		need[dif]++;
	}
	trans = MP(1, 0);
	for (int i = mid; i >= L; i--)
	{
		if (arr[i] == 0)
		{
			trans.fi *= INV;
		}
		if (arr[i] == 1)
		{
			trans.fi *= CO;
		}
		if (arr[i] == 2)
		{
			//(x+1)*a+b = a * x + a + b
			trans.se += trans.fi;
		}
		if (arr[i] == 3)
		{
			//(x - 1)*a + b = a*x - a + b
			trans.se -= trans.fi;
			trans.se += INF;
		}
		trans.fi %= INF;
		trans.se %= INF;
		freq[trans.se]++;
	}
	for (auto p : need)
	{
		ll x = p.fi; int c = p.se;
		ans += 1ll * c * freq[x];
	}
	solve(L, mid);
	solve(mid + 1, R);
}

