using Data = tuple<mll, ll, mll>;

Data prod(Data a, Data b){
	mll ha; ll pa; mll qpa; tie(ha, pa, qpa) = a;
	mll hb; ll pb; mll qpb; tie(hb, pb, qpb) = b;
	return Data(ha+qpa*hb, pa+pb, qpa*qpb);
}

void solve()
{
	ll N; string S;  cin >> N >> S;
	mll q = 999983;
	mll qq = 250007;
	mll qinv = mll(1) / q;
	mll qqinv = mll(1) / qq;

	Data plus  = Data( 1, 0, 1);
	Data minus = Data(-1, 0, 1);
	Data right = Data( 0, 1, q);
	Data left  = Data( 0,-1, qinv);
	Data pplus  = Data( 1, 0, 1);
	Data mminus = Data(-1, 0, 1);
	Data rright = Data( 0, 1, qq);
	Data lleft  = Data( 0,-1, qqinv);

	//W
	Data W = Data(0, 0, 1);
	Data WW = Data(0,0,1);
	DEPS(i, 0, N-1){
		Data si;
		Data ssi;
		if      (S[i] == '+') si = plus;
		else if (S[i] == '-') si = minus;
		else if (S[i] == '>') si = right;
		else                  si = left;
		if      (S[i] == '+') ssi = pplus;
		else if (S[i] == '-') ssi = mminus;
		else if (S[i] == '>') ssi = rright;
		else                  ssi = lleft;
		W  = prod( si, W);
		WW = prod(ssi, WW);
	}
	mll hW = get<0>(W);
	mll hWW = get<0>(WW);

	unordered_map<ll, ll> CinvMap;
	unordered_map<ll, ll> CCinvMap;
	Data X    = Data(0, 0, 1);
	Data Xinv = Data(0, 0, 1);
	Data XX    = Data(0, 0, 1);
	Data XXinv = Data(0, 0, 1);
	ll ans = 0;
	DEPS(i, 0, N-1){
		//XinvCinv
		ll h  = get<0>( Xinv).val;
		ll hh = get<0>(XXinv).val;
		CinvMap [h] ++;
		CCinvMap[hh]++;
		//X
		Data si;
		Data ssi;
		if      (S[i] == '+') si = plus;
		else if (S[i] == '-') si = minus;
		else if (S[i] == '>') si = right;
		else                  si = left;
		if      (S[i] == '+') ssi = pplus;
		else if (S[i] == '-') ssi = mminus;
		else if (S[i] == '>') ssi = rright;
		else                  ssi = lleft;
		X  = prod( si, X);
		XX = prod(ssi, XX);
		//Xinv
		Data siinv;
		Data ssiinv;
		if      (S[i] == '+') siinv = minus;
		else if (S[i] == '-') siinv = plus;
		else if (S[i] == '>') siinv = left;
		else                  siinv = right;
		if      (S[i] == '+') ssiinv = mminus;
		else if (S[i] == '-') ssiinv = pplus;
		else if (S[i] == '>') ssiinv = lleft;
		else                  ssiinv = rright;
		Xinv =  prod(Xinv,  siinv);
		XXinv = prod(XXinv, ssiinv);

		//C^-1h
		mll hX =  get<0>(X);
		mll hXX = get<0>(XX);
		mll qpXinv  = get<2>(Xinv);
		mll qpXXinv = get<2>(XXinv);
		mll hCinv  = (hW -hX)  * qpXinv;
		mll hCCinv = (hWW-hXX) * qpXXinv;
		if (CinvMap.count(hCinv.val) == 1 && CCinvMap.count(hCCinv.val) == 1){
			ll c1 =  CinvMap[ hCinv.val];
			ll c2 = CCinvMap[hCCinv.val];
			ans += min(c1, c2);
		}
	}
