{
	for (int i=0; i<9; i++) x.nw[i]=(x.nw[i]==0?mod-1:x.nw[i]-1);
	return x;
}

node f3(node x)
{
	for (int i=0; i<9; i++) x.nw[i]=1ll*x.nw[i]*v[i]%mod;
	return x;
}

node f4(node x)
{
	for (int i=0; i<9; i++) x.nw[i]=1ll*x.nw[i]*inv[i]%mod;
	return x;
}

node operator +(node a,node b)
{
