	return x == 1 ? x : (p1 - (p1 / x) * inv(p1 % x, p1)) % p1;
}
LL inc(LL &a, LL b1, LL p1) { return (a += b1) >= p1 ? a -= p1 : a; }
LL dec(LL &a, LL b1, LL p1) { return (a -= b1) < 0 ? a += p1 : a; }
