#include<bits/stdc++.h>
using namespace std;
typedef unsigned long long ull;
const ull base=998244353ull;
const ull invr=996491785301655553ull;
const int maxn=2.5e5+3;
const int maxl=5e5+10;
namespace num {
	ull _mi[maxl],*mi=_mi+maxn;
	void init() {
		mi[0]=1;
		for (int i=1;i<maxn;++i) mi[i]=mi[i-1]*base;
		for (int i=1;i<maxn;++i) mi[-i]=mi[-i+1]*invr;
	}
	int __initer__=(init(),0);
}
int n;
char s[maxn];
map<ull,int> mp;
namespace has {
	ull val[maxn],cur=0;
	int pos[maxn];
	void deal(int &p,char c) {
		if (c=='<') --p; else
		if (c=='>') ++p; else
		if (c=='+') cur+=num::mi[p]; else
		if (c=='-') cur-=num::mi[p]; else
		assert(false);
	}
	ull work() {
		int p=0;
		for (int i=1;i<=n;++i) {
			deal(p,s[i]);
			val[i]=cur;
			pos[i]=p;
		}
		return cur;
	}
}
int main() {
	scanf("%d%s",&n,s+1);
	ull all=has::work(),ans=0;
	for (int i=n;i>=0;--i) {
		ull tmp=all*num::mi[has::pos[i]]+has::val[i];
		ans+=mp[tmp];
		++mp[has::val[i]];
	}
	printf("%llu\n",ans);
	return 0;
}

