#define mul0 10009
struct RollingHash {
	static vector<int> pmo;
	int n, sz, res; vector<int> hash;
    RollingHash(int _n) : n(_n), sz(2*n+2), res(0){
        hash.resize(sz+1,0), hash[0] = 1;
		if(pmo.empty()) pmo.pb(1);
		rep(i,sz) hash[i+1]=((ll)hash[i]*mul0)%MOD;
    }
    int calc(int id, bool pl){
        if(pl) res = add(res, hash[id+n+1]);
        else res = sub(res, hash[id+n+1]);
        return res;
    }
};
vector<int> RollingHash::pmo;

map<int, int> mp;
vector<int> vec;

int main()
{
    cin.tie(0);
    ios::sync_with_stdio(false);
    int n;
    cin >> n;
    string s;
    cin >> s;
    RollingHash rh(n);
    int p = 0;
    rep(i,n){
        if(s[i] == '+'){
            vec.pb(rh.calc(p, true));
        }else if(s[i] == '-'){
            vec.pb(rh.calc(p, false));
        }else if(s[i] == '>'){
            vec.pb(vec.back());
            p++;
        }else{
            vec.pb(vec.back());
            p--;
        }
        mp[vec.back()]++;
    }
    int val = vec.back();
    ll ans = 0;
    int A = 1, B = 0;
    auto& hash = rh.hash;
    rep(i,n){
        ans += mp[mul(sub(val, B), mod_pow(A, MOD-2))];
        mp[vec[i]]--;
        if(s[i] == '+'){
            B = sub(B, hash[n+1]);
        }else if(s[i] == '-'){
            B = add(B, hash[n+1]);
        }else if(s[i] == '>'){
            int hoge = mod_pow(mul0, MOD-2);
            A = mul(A, hoge), B = mul(B, hoge);
        }else{
            A = mul(A, mul0), B = mul(B, mul0);
        }
    }
    cout << ans << "\n";
    return 0;
}

