int PowMod(int a,int b,int P)
{
	int ret=1;
	while(b)
	{
		if(b&1)
			ret=(1LL*ret*a)%P;
		b>>=1;
		a=(1LL*a*a)%P;
	}
	return ret;
}

struct Hash
{
	int a,b;
	Hash(){}
	Hash(int a,int b):a(a),b(b){}
	Hash operator + (Hash y)
	{return Hash((a+y.a)%MOD1,(b+y.b)%MOD2);}
	Hash operator - (Hash y)
	{return Hash((a-y.a+MOD1)%MOD1,(b-y.b+MOD2)%MOD2);}
	Hash operator * (Hash y)
	{return Hash((1LL*a*y.a)%MOD1,(1LL*b*y.b)%MOD2);}
	bool operator < (Hash y)const
	{return a<y.a||(a==y.a&&b<y.b);}
};

