ll beki(ll a, ll b, ll mod) {
    if(b == 0) return 1;
    if(b < 0) {
        ll INV = beki(a, -b, mod);
        return beki(INV, mod - 2, mod);
    }
    ll ret = beki(a, b / 2, mod);
    ret = ret * ret % mod;
    if(b % 2 == 1) {
        ret = ret * a % mod;
    }
    return ret;
}

