struct RollingHash {
	int n, sz, res0, res1; vector<int> hash0, hash1;
    RollingHash(int _n) : n(_n), sz(2*n+2), res0(0), res1(0){
        hash0.resize(sz+1,0), hash0[0] = 1;
        hash1.resize(sz+1,0), hash1[0] = 1;
		rep(i,sz) hash0[i+1]=((ll)hash0[i]*mul0)%MOD0;
        rep(i,sz) hash1[i+1]=((ll)hash1[i]*mul1)%MOD1;
    }
    P calc(int id, bool pl){
        if(pl) res0 = add(res0, hash0[id+n+1], MOD0), res1 = add(res1, hash1[id+n+1], MOD1);
        else res0 = sub(res0, hash0[id+n+1], MOD0), res1 = sub(res1, hash1[id+n+1], MOD1);
        return P(res0, res1);
    }
};

