int inv(int x) {
  return x == 1 ? 1 : (MOD - MOD / x) * 1LL * inv(MOD % x) % MOD;
}

LL solve() {
  int seed = primes[rand() % primes.size()];
  int inv_seed = inv(seed);
  int a = 1, b = 0;
  LL ret = 0;
  std::map<int, int> map;
  map[0] = 1;
  int c = 0;
  for (int i = n; i >= 1; -- i) {
    char ch = s[i - 1];
