    else if (ch == '-')
      c = (c + MOD - 1) % MOD;
    else if (ch == '>')
      c = c * 1LL * seed % MOD;
    else
      c = c * 1LL * inv_seed % MOD;
  }
  for (int i = n; i >= 1; -- i) {
    char ch = s[i - 1];
    if (ch == '-')
      b = (b + a) % MOD;
    else if (ch == '+')
      b = (b + MOD - a) % MOD;
    else if (ch == '<')
      a = a * 1LL * seed % MOD;
    else
      a = a * 1LL * inv_seed % MOD;
    auto it = map.find((a * 1LL * c % MOD + b) % MOD);
    if (it != map.end())
      ret += it->second;
    map[b] ++;
  }
  return ret;
}

