    mod[1] = 1e9 + 9;

    process(0);
    process(1);

    mp[ii(H[n][0],H[n][1])] = 1;

    int ans = 0;

    for(int i = 1 ; i <= n ; ++i)   {
        ans += mp[ii(H[i][0],H[i][1])];
        int res0 = H[n][0] * qpow(B,f[i],mod[0]) + H[i][0];
        int res1 = H[n][1] * qpow(B,f[i],mod[1]) + H[i][1];
        res0 %= mod[0]; res1 %= mod[1];
        mp[ii(res0,res1)]++;
    }

