	NOD(int a = 1, int b = 0) : a(a), b(b) {}
	int a, b;

	int operator () (int t) const {
		return (ll(a)*t + b) % MOD;
	}
	NOD operator * (const NOD &t) const {
		return NOD(ll(a)*t.a % MOD, (ll(a)*t.b + b) % MOD);
	}
} nod[6][MAXN], gma[6][4];

map<vector<int>, int> MP;

