LL ans;
LL mul(LL x,LL y,LL mo) {
    x%=mo;
    LL z=1;
    while (y) {
        if (y&1) z=z*x%mo;
        y>>=1;
        x=x*x%mo;
    }
    return z;
}
struct data {
    LL h1,h2;
    data operator + (const data &p) {
        data now;
        now.h1=(h1+p.h1)%mo1;
        now.h2=(h2+p.h2)%mo2;
        return now;
    }
    data operator - (const data &p) {
        data now;
        now.h1=((h1-p.h1)%mo1+mo1)%mo1;
        now.h2=((h2-p.h2)%mo2+mo2)%mo2;
        return now;
    }
    data operator * (const data &p) {
        data now;
        now.h1=(h1*p.h1)%mo1;
        now.h2=(h2*p.h2)%mo2;
        return now;
    }
    data operator * (LL x) {
        data now;
        now.h1=(h1*x%mo1+mo1)%mo1;
        now.h2=(h2*x%mo2+mo2)%mo2;
        return now;
    }
    bool operator < (const data &p) const {
        if (h1!=p.h1) return h1<p.h1;
        return h2<p.h2;
    }
}f[N+10],fac[N*2+10],invn;
vector <int> v[N+10];
map <data,int> mp;
data make(LL x,LL y) {
    data now;
    now.h1=x;
    now.h2=y;
    return now;
}
void Pre_Hash() {
    data now=make(0,0);
    int vx=N;
    f[0]=make(0,0);
    fac[0]=make(1,1);
    for (int i=1;i<=N*2;i++) fac[i]=fac[i-1]*Base;
    num[0]=vx;
    for (int i=1;i<=len;i++) {
        if (s[i]=='<') vx--;
        else if (s[i]=='>') vx++;
        else if (s[i]=='+') now=now+fac[vx];
        else now=now-fac[vx];
        num[i]=vx;
        f[i]=now;
        if (mp.find(f[i])==mp.end()) {
            mp[f[i]]=++cnt;
            v[cnt].push_back(i);
        }
        else v[mp[f[i]]].push_back(i);
    }
    invn=make(1,1);
    data invx=make(mul(Base,mo1-2,mo1),mul(Base,mo2-2,mo2));
    for (int i=1;i<=N;i++) invn=invn*invx;
}
void Calc(int x,int loc) {
    int l=0,r=v[loc].size();
    while (l<r) {
        int mid=(l+r)/2;
        if (v[loc][mid]>=x) r=mid;
        else l=mid+1;
    }
    ans+=v[loc].size()-l;
}
void work() {
    for (int i=1;i<=len;i++) {
        data now=f[len];
        now=now*fac[num[i-1]]*invn;
        now=now+f[i-1];
        if (mp.find(now)==mp.end()) continue;
        Calc(i,mp[now]);
    }
}
int main() {
    scanf("%d",&len);
    scanf("%s",s+1);
    Pre_Hash();
    work();
    printf("%lld\n",ans);
    return 0;
}

