    const vector<ull> v = {2, 325, 9375, 28178, 450775, 9780504, 1795265022};
    bool suspect(ull a, ull s, ull d, ull n) {
      if (mint64::mod != n) mint64::set_mod(n);
      mint64 one = 1, minusone = n-1, ma = a;
      auto x = mod_pow(ma, d);
      if (x.x == one.x) return true;
      for(int r = 0; r < s; r++) {
        if (x.x == minusone.x) return true;
        x = x * x;
      }
      return false;
    }

    // check if n is prime
    bool check(ull n) {
      if (n < 2 || (n > 2 && n % 2 == 0)) return false;
      ull d = n - 1;
      ull s = 0;
      while (!(d & 1)) {
        d >>= 1;
        s++;
      }
      for (auto a: v) {
        if (a >= n) break;
        if (!suspect(a, s, d, n)) return false;
      }
      return true;
    }
};

class combination {
    // factorial
    public: std::vector<mint> fact;
    public: std::vector<mint> inv;

    combination(int n) {
      fact.resize(n + 1);
      inv.resize(n + 1);
      fact[0] = 1;
      for (int i = 1; i <= n; i++) {
        fact[i] = fact[i - 1] * i;
      }
      inv[n] = mint(1) / fact[n];
      for (int i = n - 1; i >= 0; i--) {
        inv[i] = inv[i + 1] * (i + 1);
      }
    }

    // nCr
    public: mint get(int n, int r) {
      if (n < r || n < 0 || r < 0) return 0;
      return fact[n]*inv[r]*inv[n-r];
    }
    // nPr
    public: mint p(int n, int r) {
      if (n < r || n < 0) return 0;
      return fact[n]*inv[n-r];
    }
};


class FEatingSymbolsHard {
public:
    void solve(istream& cin, ostream& cout) {
      SPEED;
      int n; cin >> n;
      string s; cin >> s;
      vector<mint> base = {983, 9059, 10009, 99991};
      vector<mint> invBase(4);
      rep(i, 4) {
        invBase[i] = mint(1)/base[i];
      }
      vector<int> pos(n+1);
      vector<vector<mint>> vec(4, vector<mint>(n+1));

      rep(i, n) {
        pos[i+1] = pos[i];
        rep(k, 4) {
          vec[k][i+1] = vec[k][i];
        }
        if (s[i] == '+') {
          rep(k, 4) {
            if (pos[i] < 0) {
              vec[k][i+1] += mod_pow(invBase[k], -pos[i]);
            } else {
              vec[k][i+1] += mod_pow(base[k], pos[i]);
            }
          }
        } else if (s[i] == '-') {
          rep(k, 4) {
            if (pos[i] < 0) {
              vec[k][i+1] -= mod_pow(invBase[k], -pos[i]);
            } else {
              vec[k][i+1] -= mod_pow(base[k], pos[i]);
            }
          }
        } else if (s[i] == '<') {
          pos[i+1]--;
        } else {
          pos[i+1]++;
        }
      }

      map<mint, ll> mp[4];
      ll ans = 0;
      for(int i=n; i>=0; i--) {
        map<int, int> cnt;
        rep(k, 4) {
          mint r;
          if (pos[i] < 0) {
            r = mod_pow(invBase[k], -pos[i]);
          } else {
            r = mod_pow(base[k], pos[i]);
          }
          auto q = vec[k][i] + vec[k][n] * r;
          if (mp[k].find(q) == mp[k].end()) {
            cnt[0]++;
          } else {
            cnt[mp[k][q]]++;
          }
        }
        bool flag = false;
        for(auto e: cnt) {
          if (e.second >= 3) {
            ans += e.first;
            flag = true;
          }
        }
        assert(flag);
        rep(k, 4) {
          mp[k][vec[k][i]]++;
        }
      }

      cout << ans << endl;

    }
};

