int BinPow(int x, int m, int mod) {
    if(m == 0)
        return 1;
    else if(m == 1)
        return x % mod;
    else {
        int sum = BinPow(x, m >> 1, mod);
        sum = (1ll * sum * sum) % mod;
        if(m % 2)
            sum = (1ll * sum * x) % mod;
        return sum;
    }
}

