typedef long long ll;
typedef std::pair<int,int> pii;

const int N=250010,MOD=1000000007,g0=500002,g1=500003;

pii operator * (pii A,pii B){return pii((ll)A.x*B.x%MOD,(ll)A.y*B.y%MOD);}
pii operator + (pii A,pii B){return pii(((A.x+B.x)%MOD+MOD)%MOD,((A.y+B.y)%MOD+MOD)%MOD);}
pii operator - (pii B){return pii(-B.x,-B.y);}

ll inv(int x){return (x==1 || x==-1)?x:(-(MOD/x)*inv(MOD%x)%MOD);}

pii t0[N*2],*pow;

void initialize()
{
	pow=t0+N;

	pii tmp=pii(g0,g1);

	pow[0]=pii(1,1);
	for(int i=1;i<N;i++)
		pow[i]=pow[i-1]*tmp;

	tmp=pii((inv(g0)+MOD)%MOD,(inv(g1)+MOD)%MOD);
	for(int i=0;i<N;i++)
		pow[-i-1]=pow[-i]*tmp;
}

char s[N];
int ind[N];
pii val[N],sum[N];
int n;

ll ans;

void prework()
{
	int p=0;
	for(int i=1;i<=n;i++)
	{
		ind[i]=p,val[i]=pii(0,0);

		if(s[i]=='<')p--;
		else if(s[i]=='>')p++;
		else if(s[i]=='+')val[i]=pow[p];
		else val[i]=-pow[p];
	}

	for(int i=1;i<=n;i++)
		sum[i]=sum[i-1]+val[i];
}

std::map<pii,int> G;

void solve()
{
	ans=0;

	pii x,y;

	for(int i=n;i;i--)
	{
		G[sum[i]]++;

		x=sum[n]*pow[ind[i]];
		y=sum[i-1]+x;

		ans+=G[y];
	}
}

int main()
{
	initialize();

	scanf("%d",&n);
	scanf("%s",s+1);

	prework();
	solve();

	printf("%lld\n",ans);

	return 0;
}

