ll expo(ll a, ll e)
{
	if (e == 0)
	{
		return 1;
	}
	ll was = expo(a, e / 2);
	if (e % 2)
	{
		return was * was % INF * a % INF;
	}
	return was * was % INF;
}
ll modinv(ll a)
{
	return expo(a, INF - 2);
}
void solve(int L, int R)
{
	if (L == R)
	{
		return;
	}
	freq.clear(); need.clear();
	int mid = (L + R) >> 1;
	//start from L...mid, end at mid + 1....R
	pll trans = MP(1, 0);
	for (int i = mid + 1; i <= R; i++)
	{
		if (arr[i] == 0)
		{
			trans.fi *= INV;
			trans.se *= INV;
		}
		if (arr[i] == 1)
		{
			trans.fi *= CO;
			trans.se *= CO;
		}
		if (arr[i] == 2)
		{
			trans.se++;
		}
		if (arr[i] == 3)
		{
			trans.se--; trans.se += INF;
		}
		trans.fi %= INF;
		trans.se %= INF;
		// cerr << "from " << mid + 1 << " to " << i << " is " << trans.fi << "x + " << trans.se << endl;
		ll dif = S - trans.se + INF;
		nmod(dif, INF);
		dif *= modinv(trans.fi);
		dif %= INF;
		need[dif % LLINF]++;
	}
	trans = MP(1, 0);
	for (int i = mid; i >= L; i--)
	{
		if (arr[i] == 0)
		{
			trans.fi *= INV;
		}
		if (arr[i] == 1)
		{
			trans.fi *= CO;
		}
		if (arr[i] == 2)
		{
			//(x+1)*a+b = a * x + a + b
			trans.se += trans.fi;
		}
		if (arr[i] == 3)
		{
			//(x - 1)*a + b = a*x - a + b
			trans.se -= trans.fi;
			trans.se += INF;
		}
		trans.fi %= INF;
		trans.se %= INF;
		freq[trans.se % LLINF]++;
	}
	for (auto p : need)
	{
		ll x = p.fi; int c = p.se;
		ans += 1ll * c * freq[x];
	}
	solve(L, mid);
	solve(mid + 1, R);
}

