      delta[i] = delta[i - 1] + 1;
      sum[i] = sum[i - 1];
      break;
    }
  }
  unordered_map<li, int> cnt;
  li ans = 0;
  for (int i = n; i >= 0; --i) {
    if (i < n) {
      li target = Add(sum[i] + Mul(pow[delta[i]], sum[n]));
      ans += cnt[target];
    }
    ++cnt[sum[i]];
  }
  printf("%lld\n", ans);
